package slidingwindow;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class CountAnagrams {

    private static List<Integer> countAnagrams(String s, String p) {

        int count = 0;
        for (int i = 0; i < s.chars().count(); i++) {
            if(s.charAt(i) == 'b'){
                System.out.println("found at: "+ i);
                count++;
            }
        }
        System.out.println(count);

        // first point i an j both to 0
        int i = 0, j = 0;

        int k = p.length();



        Map<Character, Integer> countMap = new HashMap<>();

        List<Integer> res = new ArrayList<>();

        if(p.length() > s.length()){
            res.add(0);
            return res;
        }

        while (j - i + 1 <= k) {
            Character ch = s.charAt(j);
            countMap.merge(ch, 1, Integer::sum);
            j++;
        }

        for (; j < s.length(); j++) {

            if (patternFound(new HashMap<>(countMap), p)) {
                res.add(i);
            }

            countMap.put(s.charAt(i), countMap.get(s.charAt(i)) - 1);
            countMap.merge(s.charAt(j), 1, Integer::sum);
            countMap.values().removeIf(f -> f == 0f);
            i++;
        }

        if (patternFound(new HashMap<>(countMap), p)) {
            res.add(i);
        }

        return res;
    }

    public static void main(String[] args) {
        System.out.println(countAnagrams("",
                ""));
    }

    private static boolean patternFound(Map<Character, Integer> countMap, String pattern) {

        int size = countMap.size();

        for (int i = 0; i < pattern.length(); i++) {

            char ch = pattern.charAt(i);

            if(countMap.get(ch) != null){
                countMap.put(ch, countMap.get(ch) - 1);

                if(countMap.get(ch) == 0) {
                    size--;
                }
            }
        }
        return size == 0;
    }

}